definition.script:
stage 'prepare'
parallel 'pull tools': {
  node {
    sh 'wget -q http://repo1.maven.org/maven2/org/codehaus/sonar/runner/sonar-runner-dist/2.4/sonar-runner-dist-2.4.zip'
    sh 'unzip -o sonar-runner-dist-2.4.zip'
    stash includes: 'sonar-runner-2.4/**', name: 'sonar-runner'
  }
}, 'checkout': {
  node {
    git 'https://github.com/SonarSource/sonar-examples.git'
    stash includes: 'projects/**', name: 'projects'
  }
}
stage 'test'
  node {
    unstash 'projects'
    unstash 'sonar-runner'
    dir('projects/languages/java/sonar-runner/java-sonar-runner-simple/') {
      sh '../../../../../sonar-runner-2.4/bin/sonar-runner'
    }
  }
